import Header from './Header.js';
import {useState} from 'react';
import Button from '@material-ui/core/Button';

const Platform = (props) => {
    const platform = props.match.params.platform;
    const [platformColumns, setPlatformColumns] = useState(JSON.parse(localStorage.getItem(`${platform};columns`)) ?? []);
    const [addColumn, setAddColumn] = useState(false)

    const onHitEnter = (name) => {
        setPlatformColumns([...platformColumns, name]);
    }

    const toggleAddColumn = () => {
        setAddColumn(!addColumn);
    }

    const onKeyDownInputHandler = (e) => {
        if (e.key === 'Enter') {
            onHitEnter(e.target.value);
        }
    }

    return (<div>
        <Header />
            <Button onClick={toggleAddColumn} variant="contained" color='primary'>Add a column</Button>
            {addColumn && <div><input onKeyDown={onKeyDownInputHandler} type='text'></input><button onClick={toggleAddColumn}>x</button></div>}
        </div>)
}

export default Platform;
