import Header from './Header';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import '../Styles/Home.css';
import {useState, useEffect} from 'react'

const Home = (props) => {
    const localStoragePlatforms = JSON.parse(localStorage.getItem('platforms'));
    const [addingPlatform, setAddingPlatform] = useState(false);
    const [newPlatformContent, setNewPlatformContent] = useState('');
    const [platforms, setPlatforms] = useState(localStoragePlatforms ?? []);

    const onHitEnter = (name) => {
        setPlatforms([...platforms, name])
        localStorage.setItem('platforms', JSON.stringify([...platforms, name]))
        toggleAddPlatform()
    }

    const deletePlatform = (e) => {
        console.log(e)
        name = ''
        localStorage.setItem('platforms', platforms.filter((platform) => platform !== name));
        setPlatforms(platforms.filter((platform) => (platform !== name)));
    }

    const onInputHandler = (e) => {
        console.log(e.key)
        if (e.key === 'Enter')
            onHitEnter(e.target.value);
    }

    const toggleAddPlatform = () => {
        setAddingPlatform(!addingPlatform)
    } 

    return (
    <div id="homeMainDiv">
        <Header />
        <div>
            <Button variant="contained" color='primary' onClick={toggleAddPlatform}>Add a platform </Button>
        </div>
        {addingPlatform &&
            <div>
                <input type='text' onKeyDown={onInputHandler} placeholder='Platform name'></input> 
                <button onClick={toggleAddPlatform}>x</button>
            </div>}
        {platforms.map((platform) => {
            return (
            <div>
                 <Button variant="outlined" color='primary'>{platform}</Button>
                 <IconButton aria-label='delete' onClick={deletePlatform} name={platform}><DeleteIcon /></IconButton>
            </div>);
        }
        )}
    </div>)
}

export default Home;
