import "../Styles/Platform.css";
import { useState } from "react";
import Header from "./Header.js";
import PlatformTable from "./PlatformTable";
import Edit from "@material-ui/icons/Edit";
import Button from "@material-ui/core/Button";

const Platform = (props) => {
  const platform = props.match.params.platform;
  const [platformColumns, setPlatformColumns] = useState(
    JSON.parse(localStorage.getItem(`${platform};columns`)) ?? []
  );
  const [platformEntries, setPlatformEntries] = useState(
    JSON.parse(localStorage.getItem(`${platform};entries`)) ?? []
  );
  const [addColumn, setAddColumn] = useState(false);
  const [addEntry, setAddEntry] = useState(false);
  const [tableEditable, setTableEditable] = useState(false);

  const submitNewColumn = (e) => {
    e.preventDefault();

    const name = e.target.children[0].value;

    if (name === "") return;
    setPlatformColumns([...platformColumns, name]);
    localStorage.setItem(
      `${platform};columns`,
      JSON.stringify([...platformColumns, name])
    );
    e.target.children[0].value = "";
  };

  const toggleAddColumn = () => {
    setAddEntry(false);
    setAddColumn(!addColumn);
  };

  const onClickEdit = () => {
    console.log("gone");
    setTableEditable(!tableEditable);
  };

  const submitNewEntry = (e) => {
    e.preventDefault();
    const children = [...e.target.children]
      .slice(0, -1)
      .map((child) => child.value);
    localStorage.setItem(
      `${platform};entries`,
      JSON.stringify([...platformEntries, children])
    );
    setPlatformEntries([...platformEntries, children]);
    const toClear = [...e.target.children];
    toClear.forEach((elem) => {
      elem.value = "";
    });
    e.target.children[0].focus();
  };

  const toggleAddEntry = (e) => {
    setAddColumn(false);
    <div></div>;
    setAddEntry(!addEntry);
  };

  return (
    <div id="mainDivPlatform">
      <div id="headDiv">
        <Header />
        <span id="platformName">{platform}</span>
      </div>
      <div id="topTools">
        <div id="topButtons">
          <Button onClick={toggleAddColumn} variant="contained" color="primary">
            Add a column
          </Button>
          <Button onClick={toggleAddEntry} variant="contained" color="primary">
            Add an entry
          </Button>
          <Button variant="contained" color="secondary">
            <Edit onClick={onClickEdit}></Edit>
          </Button>
        </div>
        {addEntry && (
          <form onSubmit={submitNewEntry}>
            {platformColumns.map((name) => {
              return (
                <input
                  className="topInput"
                  placeholder={name}
                  key={"input" + name}
                ></input>
              );
            })}
            <button className="submitButton" type="submit">
              +
            </button>
          </form>
        )}
        {addColumn && (
          <form onSubmit={submitNewColumn}>
            <input
              className="topInput"
              type="text"
              placeholder="Column name"
            ></input>
            <button className="submitButton" type="submit">
              +
            </button>
          </form>
        )}
      </div>
      {platformEntries.length === 0 ? (
        <div>Click on the "add an entry" button to display the Table.</div>
      ) : (
        <PlatformTable
          setPlatformColumns={setPlatformColumns}
          setPlatformEntries={setPlatformEntries}
          platformColumns={platformColumns}
          platform={platform}
        />
      )}
    </div>
  );
};

export default Platform;
