import Header from './Header';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import '../Styles/Home.css';
import {Link} from 'react-router-dom';
import {useState} from 'react';

const Home = () => {
    const localStoragePlatforms = JSON.parse(localStorage.getItem('platforms'));
    const [addingPlatform, setAddingPlatform] = useState(false);
    const [platforms, setPlatforms] = useState(localStoragePlatforms ?? []);

    const onHitEnter = (name) => {
        setPlatforms([...platforms, name])  
        localStorage.setItem('platforms', JSON.stringify([...platforms, name]))
        toggleAddPlatform()
    }

    const deletePlatform = (name) => {
        const res = window.confirm(`Do you really want to delete the platform ${name} ?`)
        if (res) {
            localStorage.setItem('platforms', JSON.stringify(platforms.filter((platform) => platform !== name)));
            localStorage.removeItem(`${name};columns`);
            localStorage.removeItem(`${name};entries`);
            setPlatforms(platforms.filter((platform) => (platform !== name)));
        }
    }

    const onInputHandler = (e) => {
        if (e.key === 'Enter')
            onHitEnter(e.target.value);
    }

    const toggleAddPlatform = () => {
        setAddingPlatform(!addingPlatform)
    } 

    return (
    <div id="homeMainDiv">
        <Header />
        <div>
            <Button variant="contained" color='primary' onClick={toggleAddPlatform}>Add a platform </Button>
        </div>
        {addingPlatform &&
            <div>
                <input type='text' onKeyDown={onInputHandler} placeholder='Platform name'></input> 
                <button onClick={toggleAddPlatform}>x</button>
            </div>}
        {platforms.map((platform) => {
            return (
            <div className="platformLink" key={platform}>
                 <Link to={`/platform/${encodeURI(platform)}`} > <Button variant="outlined" color='primary'>{platform}</Button></Link>
            <IconButton aria-label='delete' onClick={() => {deletePlatform(platform)}} name={platform}><DeleteIcon /></IconButton>
            </div>);
        }
        )}
    </div>)
}

export default Home;
