import Header from './Header';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import '../Styles/Home.css';
import {useState, useEffect} from 'react'

const Home = (props) => {
    const [addingPlatform, setAddingPlatform] = useState(false);
    const [newPlatformContent, setNewPlatformContent] = useState('');

    const toggleAddPlatform = () => {
        setAddingPlatform(!addingPlatform)
    } 

    return (
    <div id="homeMainDiv">
        <Header />
            <div>

         <Button variant="contained" color='primary' onClick={toggleAddPlatform}>Add a platform </Button>
            </div>
         {addingPlatform && <div><input type='text' placeholder='Platform name'></input>  <button onClick={toggleAddPlatform}>x</button></div>}
         <div>

         <Button variant="outlined" color='primary'>Platform 2 </Button>
         </div>
         <div>

         <Button variant="outlined" color='primary'>Platform 3 </Button>
         </div>
    </div>)
}

export default Home;
