import Header from "./Header.js";
import "bootstrap/dist/css/bootstrap.min.css";
import "../Styles/Platform.css";
import { useState } from "react";
import Button from "@material-ui/core/Button";
import { Table } from "reactstrap";

const Platform = (props) => {
  const platform = props.match.params.platform;
  const [platformColumns, setPlatformColumns] = useState(
    JSON.parse(localStorage.getItem(`${platform};columns`)) ?? []
  );
  const [platformEntries, setPlatformEntries] = useState(
    JSON.parse(localStorage.getItem(`${platform};entries`)) ?? []
  );
  const [addColumn, setAddColumn] = useState(false);
  const [addEntry, setAddEntry] = useState(false);

  const onHitEnter = (name) => {
    setPlatformColumns([...platformColumns, name]);
    localStorage.setItem(
      `${platform};columns`,
      JSON.stringify([...platformColumns, name])
    );
  };

  const toggleAddColumn = () => {
    setAddEntry(false);
    setAddColumn(!addColumn);
  };

  const submitNewEntry = (e) => {
    e.preventDefault();
    const children = [...e.target.children]
      .slice(0, -1)
      .map((child) => child.value);
    localStorage.setItem(
      `${platform};entries`,
      JSON.stringify([...platformEntries, children])
    );
    setPlatformEntries([...platformEntries, children]);
    const toClear = [...e.target.children];
    toClear.forEach((elem) => {
      elem.value = "";
    });
    e.target.children[0].focus();
  };

  const onKeyDownInputHandler = (e) => {
    if (e.key === "Enter") {
      onHitEnter(e.target.value);
      setAddColumn(!addColumn);
    }
  };

  const toggleAddEntry = (e) => {
    setAddColumn(false);
    setAddEntry(!addEntry);
  };

  return (
    <div id="mainDivPlatform">
      <Header />
      <div id="topButtons">
        <Button onClick={toggleAddColumn} variant="contained" color="primary">
          Add a column
        </Button>
        <Button onClick={toggleAddEntry} variant="contained" color="primary">
          Add an entry
        </Button>
      </div>
      {addEntry && (
        <form onSubmit={submitNewEntry}>
          {platformColumns.map((name) => {
            return <input placeholder={name} key={"input" + name}></input>;
          })}
          <button type="submit">+</button>
        </form>
      )}
      {addColumn && (
        <div>
          <input
            onKeyDown={onKeyDownInputHandler}
            type="text"
            placeholder="Column name"
          ></input>
          <button onClick={toggleAddColumn}>x</button>
        </div>
      )}
      <Table
        id="platformTable"
        size="sm"
        responsive={true}
        striped={true}
        dark={true}
      >
        <thead>
          <tr>
            <th>#</th>
            {platformColumns.map((name) => {
              return <th key={name}>{name}</th>;
            })}
          </tr>
        </thead>
        <tbody>
          {platformEntries.map((entry, mainIndex) => {
            return (
              <tr>
                <td>{mainIndex}</td>
                {entry.map((current, index) => (
                  <td contentEditable={true}>{current}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </Table>
    </div>
  );
};

export default Platform;
