import Header from './Header'
import Button from '@material-ui/core/Button'
import Container from '@material-ui/core/Container'
import '../Styles/Home.css';
import {useState, useEffect} from 'react'

const Home = (props) => {
    const [addingPlatform, setAddingPlatform] = useState(false);
    const [newPlatformContent, setNewPlatformContent] = useState('');

    const toggleAddPlatform = () => {
        setAddingPlatform(!addingPlatform)
    } 

    return (
    <div>
        <Header />
        <Container >
         <Button variant="contained" color='primary' onClick={toggleAddPlatform}>Add a platform </Button>
         {addingPlatform && <div><input type='text'></input>  <button onClick={toggleAddPlatform}>x</button></div>}
         <Button variant="contained" color='primary'>Platform 2 </Button>
         <Button variant="contained" color='primary'>Platform 3 </Button>
         </Container>
    </div>)
}

export default Home;
